INSERT INTO `test_restaurants` VALUES (1,'Bar M',4.50,'bar','100台灣台北市中正區羅斯福路二段20號','0966 024 881','[\"星期一: 18:30 – 00:30\", \"星期二: 18:30 – 00:30\", \"星期三: 18:30 – 00:30\", \"星期四: 18:30 – 00:30\", \"星期五: 18:30 – 01:30\", \"星期六: 18:30 – 01:30\", \"星期日: 18:30 – 00:00\"]','[{\"text\": \"【食記&品飲】\\nBar M & Whisky Salon\\n\\n山盛り！いくら丼（鮭魚卵丼飯）\\n這是一碗放滿滿日本產鮭魚卵的日本米，每天限量三碗；鮭魚卵清涼的感覺以及鮮味、配上熱呼呼且帶有嚼勁的米飯，在口中無論是口感還是溫度通通都是一絕，旁邊的山葵和醃蘿蔔也起了很強的提味效果，再度拉升品味的層次。\\n\\n牛小排的口感極度軟嫩，輕輕咬就能把肉撕開，而其油脂感並不太強烈，不太會搶調酒的味道，反而起到了經歷酒精刺激的味覺校正效果。\\n\\n【品飲】\\n麝香葡萄調酒有著鮮明的琴酒杜松子氣息，本來還感受不到麝香葡萄的氣息，當酒稍微回溫、以及吃了那顆清甜的麝香葡萄之後，就在酒中重新找到了那股清甜味。\\n\\nNegroni剛開始喝的時候，柑橘氣息和草本氣息的表現很平衡，然而吞嚥後會回沖一股很強的酒精感上來；待融冰程度增加之後，酒精感變得越來越溫潤，直到成為宜人的樣貌。\", \"author_name\": \"Water “H2O” Chang\"}, {\"text\": \"酒跟氣氛都不錯，但食物不一定要點?\\n章魚燒的章魚很小塊，麵衣偏厚偏軟，不要用夜市章魚燒的標準去想像它，容易失望；\\n起司臘腸披薩有點鹹?；\\n牛肉捲算是三個食物裡面比較ok的。\", \"author_name\": \"Y L\"}, {\"text\": \"來過很多次，店員們超級親切！\\n調酒很有水準，也可以客製調酒！\\n許願可以有一些爵士音樂?\", \"author_name\": \"TING CHIA LI\"}, {\"text\": \"生意很好，客人幾乎都女生，難怪酒感弱弱的。主要是環境不舒適，委屈在倉庫喝酒的感覺。\", \"author_name\": \"UbiQuiTousLy\"}]','[{\"text\": \"長島冰茶愛好者\\n但這家的酒精感太強\\n不太能接受\\n章魚燒我們三個朋友一起分食 也吃不太習慣\", \"author_name\": \"徐智偉Max\"}]','[{\"widthPx\": 4000, \"heightPx\": 6000, \"photo_reference\": \"AUGGfZk1B_T6PxIDA-rw8iUn6dmGyEaEwodTu0l1j3ByJwj_4nx4rHLoTyvLOh9l04HVz1cim1iiiXWKw51Db4fzKaSFanGpzDmeDV5df4zG3V-kWQR3XAhzoIU6U8QDhNNMTUwmSJAiXda1o1WNpytDZxsoI0zD-31znqAelzvf5XdZz7Kp\"}, {\"widthPx\": 4000, \"heightPx\": 6000, \"photo_reference\": \"AUGGfZlsHcYqd6Ldeduzgf3epgz3v7QgMdH-KEyIZayY3uaSHqIgNG9Rz5KsZDbOZ9jRv1-S0cDSUsZwIJHRuvVMrAJwSQMhStyo2qrxBuGicoliwmLjH4JvDafmF1OQjxDi3KVkn2rzQjUTIjN7iq9zmEH6Ki2g-5y6up-R7tUilBINfFDp\"}, {\"widthPx\": 4000, \"heightPx\": 6000, \"photo_reference\": \"AUGGfZmaVfkh4f2ajxkb8sfnlOVGGBFT3vbgFZcEBZoCSJVihJefzGDO1WqX3RuHBpYV3CY1jA_I8cc4lmxLZamSlnKXesfkaQ2YQbtn53tgg-aJ3jAg8_2vjaTR_lyq8yWJayvjtNbuLkSW6TiVrkGNQBANTnq_YstZUZoz20QSamt3zGs0\"}, {\"widthPx\": 4000, \"heightPx\": 6000, \"photo_reference\": \"AUGGfZk1-62ZepQTChqgcDgnPkUdHDyXuyo8W